const RenderElement = ({ text }: { text: string }) => {
  return (
    <div className={styles.renderElement}>
      {getIcon(text)}
      <span>{capitalize(text)}</span>
    </div>
  );
};

const BusinessAssesPopup = ({ handleClose }: { handleClose: () => void }) => {
  const defaultOption = {
    display: { displayText: 'Select the network / platform' },
    render: { text: 'Select the network / platform' },
  };
  const [selectedNetwork, setSelectedNetwork] = useState(defaultOption);
  return (
    <div className={styles.popup}>
      <div className={styles.container}>
        <div className={styles.header}>
          <p>Add yours business assets details</p>
          <button onClick={handleClose}>
            <FaXmark />
          </button>
        </div>
        <div className={styles.body}>
          <CustomLabelWrapper label="Network/Platform">
            <CustomSelect3
              DisplayElement={DisplayElement}
              RenderElement={RenderElement}
              childrens={childrens}
              defaultOption={defaultOption}
              handleSelect={a => {
                setSelectedNetwork(a);
              }}
            />
          </CustomLabelWrapper>
          {getForm(selectedNetwork.display.displayText)}
        </div>
        <div className={styles.footer}>
          <button onClick={handleClose}>Cancel</button>
          <button onClick={handleClose}>Submit</button>
        </div>
      </div>
    </div>
  );
};

const getForm = (network: string) => {
  const lower = network.toLowerCase();
  switch (lower) {
    case 'meta':
    case 'tiktok':
      return (
        <>
          <p>
            Save your existing {capitalize(network)} Business Center details
          </p>
          <CustomInput
            label="Business Manager Name"
            placeholder={`Enter your ${capitalize(
              network
            )} Business Manager's name`}
          />
          <CustomInput
            label="Business Center ID"
            placeholder={`Enter your ${capitalize(network)} Business Center ID`}
          />
        </>
      );
    case 'google':
    case 'bing':
    case 'snapchat':
    default:
      return (
        <>
          <p>Save a {capitalize(network)} profile</p>
          <span>
            Add emails of {capitalize(network)} accounts that will be assigned
            to the advertising account.
          </span>
          <CustomInput
            label={`${capitalize(network)} Email`}
            placeholder={`Enter your ${capitalize(network)} Email`}
          />
        </>
      );
  }
};

export default BusinessAssesPopup;
